mapper.update:
update requests
set
	status_no = #{statusNo}
where rno = #{rno}

mapper.select:
select count(*)
from request_process


		developer = #{mid}
	and dev_expect_date is null


		tester = #{mid}
	and test_expect_date is null


		user_tester = #{mid}
	and usertest_expect_date is null


		distributor = #{mid}
	and dist_expect_date is null

select count(*)

from request_process 


from request_process rp, requests r



		r.client = #{mid}
	and all_comp_date is null 
	and all_expect_Date is not null
	and rp.rno = r.rno


		 all_expect_date is not null and all_comp_date is null


		developer = #{mid}
	and dev_expect_date is not null and dev_comp_date is null


		tester = #{mid}
	and test_expect_date is not null and test_comp_date is null


		user_tester = #{mid}
	and usertest_expect_date is not null and usertest_comp_date is null


		distributor = #{mid}
	and dist_expect_date is not null and dist_comp_date is null

select count(*)

from request_process 


from request_process rp, requests r



		r.rno = rp.rno
	and (rp.all_comp_date is not null OR r.status_no=12)
	and r.client = #{mid}


		all_comp_date is not null


		developer = #{mid}
	and dev_comp_date is not null


		tester = #{mid}
	and test_comp_date is not null


		user_tester = #{mid}
	and usertest_comp_date is not null


		distributor = #{mid}
	and dist_comp_date is not null

select r.req_title as "reqTitle", rp.req_type as "reqType", rp.priority as "priority", r.req_date as "reqDate", rp.all_expect_date as "allExpectDate", m.mname as "mname", st.status_name as "statusName", r.rno as "rno", rp.developer as "developer" rp.user_tester as "userTester" rp.tester as "tester" rp.distributor as "distributor" from requests r, request_process rp, systems s, members m, status st where r.rno = rp.rno and r.sno = s.sno and rp.developer = m.mid and rp.user_tester = m.mid and rp.tester = m.mid and rp.distributor = m.mid and r.status_no = st.status_no and r.sno = #{member.sno}
and rp.developer = #{member.mid} and rp.user_tester = #{member.mid} and rp.tester = #{member.mid} and rp.distributor = #{member.mid}
order by r.req_date OFFSET (#{pager.pageNo} - 1) * #{pager.rowsPerPage} ROWS FETCH NEXT #{pager.rowsPerPage} ROWS ONLY

select count(*)
from requests r, request_process rp, systems s, members m, status st 
where r.rno = rp.rno
and r.sno = s.sno
  and rp.developer = m.mid
 and rp.user_tester  = m.mid
 and rp.tester = m.mid
 and rp.distributor  = m.mid
and r.status_no = st.status_no
and r.sno = #{member.sno}

	 and rp.developer = #{member.mid}
	 and rp.user_tester  = #{member.mid}
	 and rp.tester = #{member.mid}
	 and rp.distributor  = #{member.mid}
