mapper.update:
		update requests
		    set REQ_TITLE = #{reqTitle}, 
		        REQ_CONTENT = #{reqContent}, 
		        REQ_EXPECT_DATE = #{reqExpectDate}, 
		        SNO = #{sno}
    	where rno = #{rno}

mapper.select:
select NEXT_STATUS
from (
select rownum as rnum, NEXT_STATUS,CHANGE_DATE
from(
select rownum, NEXT_STATUS,CHANGE_DATE from status_histories
where rno = #{rno}
order by CHANGE_DATE desc
)
order by rnum
)
where rnum=1

select max(rno) as "rno"
from requests
where client = #{client}

select count(*) from ( select rownum as rnum, r.RNO, r.REQ_DATE, r.REQ_TITLE, r.REQ_CONTENT, r.CLIENT, r.SNO, r.REQ_EXPECT_DATE, r.STATUS_NO, rp.REQ_TYPE, rp.DEVELOPER, rp.TESTER, rp.DISTRIBUTOR, rp.PM, rp.USER_TESTER from requests r left outer join request_process rp on r.RNO = rp.RNO inner join systems s on r.sno = s.sno order by r.req_date desc )
and sno = #{listFilter.sno}
and sno = #{member.sno}
and developer = #{member.mid}
and tester = #{member.mid}
and user_tester = #{member.mid}
and distributor = #{member.mid}
and REQ_TYPE = #{listFilter.reqType}
and REQ_DATE <= to_date(#{listFilter.dateLast}, 'yyyy-MM-dd') + 1
and to_date(#{listFilter.dateFirst}, 'yyyy-MM-dd') <= REQ_DATE
and REQ_DATE between to_date(#{listFilter.dateFirst}, 'yyyy-MM-dd') and to_date(#{listFilter.dateLast}, 'yyyy-MM-dd')+1
and status_no in (1)
and status_no in (2, 3, 4)
and status_no in (5, 6)
and status_no in (7, 8)
and status_no in (9, 10)
and status_no in (11, 13)
and status_no in (12)

select j.RNO, j.REQ_DATE as "reqDate" , j.REQ_TITLE as "reqTitle", j.REQ_CONTENT as "reqContent", j.CLIENT, j.SNO, j.REQ_EXPECT_DATE as "reqExpectDate", j.STATUS_NO as "statusNo", j.REQ_TYPE as "reqType", j.DEVELOPER, j.TESTER, j.DISTRIBUTOR, j.PM, j.USER_TESTER as "userTester", j.SYSTEM_NAME as "systemName" from ( select r.RNO, r.REQ_DATE, r.REQ_TITLE, r.REQ_CONTENT, r.CLIENT, r.SNO, r.REQ_EXPECT_DATE, r.STATUS_NO, rp.REQ_TYPE, rp.DEVELOPER, rp.TESTER, rp.DISTRIBUTOR, rp.PM, rp.USER_TESTER, s.SYSTEM_NAME from requests r left outer join request_process rp on r.RNO = rp.RNO inner join systems s on r.sno = s.sno
and r.sno = #{listFilter.sno}
and r.sno = #{member.sno}
and developer = #{member.mid}
and tester = #{member.mid}
and user_tester = #{member.mid}
and distributor = #{member.mid}
and REQ_TYPE = #{listFilter.reqType}
and REQ_DATE <= to_date(#{listFilter.dateLast}, 'yyyy-MM-dd') + 1
and to_date(#{listFilter.dateFirst}, 'yyyy-MM-dd') <= REQ_DATE
and REQ_DATE between to_date(#{listFilter.dateFirst}, 'yyyy-MM-dd') and to_date(#{listFilter.dateLast}, 'yyyy-MM-dd')
and status_no in (1)
and status_no in (2, 3, 4)
and status_no in (5, 6)
and status_no in (7, 8)
and status_no in (9, 10)
and status_no in (11, 13)
and status_no in (12)
order by r.req_date desc ) j offset (#{pager.pageNo} - 1) * #{pager.rowsPerPage} ROWS fetch first (#{pager.rowsPerPage}) rows only

select FNO, SAVED_DATE as "savedDate", FILE_NAME as "fileName", FILE_TYPE as "fileType", FILE_DATA as "fileData"
	from STATUS_HISTORIES_FILES
	where hno = #{hno}

select FNO, SAVED_DATE as "savedDate", FILE_NAME as "fileName", FILE_TYPE as "fileType", FILE_DATA as "fileData"
	from STATUS_HISTORIES_FILES
	where fno = #{fno}

		select count(*)
		from requests r left join request_process rp
                on r.rno = rp.rno
                inner join systems s
                on r.sno = s.sno



			          #{item}



        		and developer = #{member.mid}
        		and r.sno = #{member.sno}


        		and tester = #{member.mid}
        		and r.sno = #{member.sno}


        		and user_tester = #{member.mid}
        		and r.sno = #{member.sno}


        		and distributor = #{member.mid}
        		and r.sno = #{member.sno}

select r.rno as "rno", req_date as "reqDate", req_title as "reqTitle", req_content as "reqContent", client as "client", r.sno as "sno", req_expect_date as "reqExpectDate", r.status_no as "statusNo", req_type as "reqType", developer as "developer", tester as "tester", distributor as "distributor", pm as "pm", user_tester as "userTester", system_name as "systemName" from requests r left join request_process rp on r.rno = rp.rno inner join systems s on r.sno = s.sno
#{item}
and developer = #{member.mid} and r.sno = #{member.sno}
and tester = #{member.mid} and r.sno = #{member.sno}
and user_tester = #{member.mid} and r.sno = #{member.sno}
and distributor = #{member.mid} and r.sno = #{member.sno}
order by req_date desc offset (#{pager.pageNo} - 1) * #{pager.rowsPerPage} ROWS fetch first (#{pager.rowsPerPage}) rows only
